<?phprequire_once('app/Mage.php'); umask(0);Mage::app();$collectionSimple = Mage::getResourceModel('catalog/product_collection')->addAttributeToFilter('type_id', array('eq' => 'simple'));foreach ($collectionSimple as $_configurableproduct) {	$product = Mage::getModel('catalog/product')->load($_configurableproduct->getId());    echo $product->getId() . '<hr/>';    $stock = Mage::getModel('cataloginventory/stock_item')->loadByProduct($product->getId());    $stock->setQty(10);    $stock->save();}